const { activities, comments, stageHistory } = $('Buscar dados da Timeline do Lead').first().json

function toBrazilDate(dateString) {
  const date = new Date(dateString);
  date.setHours(date.getHours() - 3);
  return date;
}

const data = []

async function mapperActivities() {
  for (const item of activities) {
    const createdAt = toBrazilDate(item.CREATED)
    const lastUpdatedAt = toBrazilDate(item.LAST_UPDATED)
    const startedAt = toBrazilDate(item.START_TIME)
    const finishedAt = toBrazilDate(item.END_TIME)

    const mappedActivity = {
      historyName: 'activity',
      registerId: item.ID, 
      subject: item.SUBJECT,
      typeId: item.PROVIDER_TYPE_ID,
      isCompleted: item.COMPLETED == 'Y',
      createdAt, 
      lastUpdatedAt,
      startedAt,
      finishedAt
    }

    if (item.CALL_DETAILS) {
      const call = item.CALL_DETAILS

      mappedActivity.callDetails = {
        registerId: call.ID,
        category: call.CALL_CATEGORY,
        durationInSeconds: call.CALL_DURATION,
        callUrl: call.CALL_RECORD_URL,
        callCost: call.COST, 
        callCostCurrency: call.COST_CURRENCY, 
        callKey: call.CALL_ID
      }
    }

    data.push(mappedActivity)
  }
}
function mapperComments() {
  for (const item of comments) {
    const createdAt = toBrazilDate(item.CREATED)

    const mappedComments = {
      historyName: 'comment',
      registerId: item.ID,
      createdAt,
      comment: item.COMMENT
    }

    data.push(mappedComments)
  }
}
function mapperStageHistory() {
  for (const item of stageHistory) {
    const createdAt = toBrazilDate(item.CREATED_TIME) 

    const mappedStageHistory = {
      historyName: 'stageHistory',
      registerId: item.ID,
      status: item.STATUS_ID,
      createdAt
    }

    data.push(mappedStageHistory)
  }
}

await mapperActivities();
await mapperComments();
await mapperStageHistory();

return data

