const sleep = (ms) => new Promise((r) => setTimeout(r, ms));

async function bitrixList(method, params) {
  const url = `${$env.BITRIX_URL}${method}`;
  let start = 0;
  const out = [];
  let tries = 0;

  while (true) {
    const body = { ...params };
    if (start) body.start = start;

    try {
      const res = await this.helpers.httpRequest({
        method: "POST",
        url,
        json: true,
        body,
        timeout: 30000,
      });

      let items = Array.isArray(res?.result) ? (res.result ?? []) : [];
      items = !items.length && (typeof res?.result == 'object') ? res?.result.items : items 
      out.push(...items);

      if (res?.next !== undefined && res.next !== null) {
        start = res.next;
        tries = 0;
        await sleep(200);
      } else {
        break;
      }
    } catch (err) {
      if (++tries <= 3) {
        await sleep(500 * tries);
        continue;
      }
      throw new Error(`Falha em ${method}: ${err?.message || String(err)}`);
    }
  }
  return out;
}

