function removeEmojis(str) {
  const re = /(?:[\u{1F1E6}-\u{1F1FF}]{2}|[#*0-9]\uFE0F?\u20E3|\p{Extended_Pictographic}(?:\uFE0F|\u200D(?:\p{Extended_Pictographic}|\u2640|\u2642)\uFE0F?)*|\uFE0F|\u200D)/gu;
  return str.replace(re, '');
}


function markdownToBBCode(markdown) {
  return markdown
    // TÃ­tulos
    .replace(/^### (.*$)/gim, '[b]$1[/b]')
    .replace(/^## (.*$)/gim, '[b]$1[/b]')
    .replace(/^# (.*$)/gim, '[b]$1[/b]')

    // Negrito
