function cleanText(s) {
  if (!s) return "";
  // remove markdown leve e m√∫ltiplos espa√ßos
  s = s.replace(/[\*_~`]+/g, "")
       .replace(/\s+/g, " ")
       .trim();
  // corta para 220 chars
  if (s.length > 220) s = s.slice(0, 217) + "‚Ä¶";
  return s;
}

function roleFrom(msg) {
  // dataJson.key.fromMe === true => agente (a); caso contr√°rio, usu√°rio (u)
  const fromMe = msg?.dataJson?.key?.fromMe === true || msg?.fromMe === true;
  return fromMe ? "a" : "u";
}

function tsFrom(msg) {
  return msg.updatedAt || msg.createdAt || null;
}

function isNoise(t) {
  if (!t) return true;
  const low = t.toLowerCase();
  const greetings = ["oi", "ola", "ol√°", "opa, tudo bem?", "ooooii"];
  if (greetings.includes(low)) return true;
  if (low.length < 2) return true;
  return false;
}

const raw = JSON.parse($('Webhook').first().json.body.lastMessages) || [];
console.log({ raw })
const mapped = raw.map(m => ({
  r: roleFrom(m),
  t: cleanText(m.body || m?.dataJson?.message?.conversation || ""),
  ts: tsFrom(m)
}))
.filter(m => !isNoise(m.t));

// manter somente √∫ltimas 10 √∫teis (ordenar por ts asc e pegar tail)
mapped.sort((a,b) => (new Date(a.ts||0)) - (new Date(b.ts||0)));
const trimmed = mapped;

// mesclar mensagens consecutivas do mesmo role quando for continua√ß√£o simples
const merged = [];
for (const m of trimmed) {
  const last = merged[merged.length - 1];
  if (last && last.r === m.r && (last.t + " " + m.t).length <= 220) {
    last.t = cleanText(last.t + " " + m.t);
    last.ts = m.ts; // manter o timestamp mais recente do bloco
  } else {
    merged.push({...m});
  }
}

return {
  chatInput: $input.first().json.chatInput,
  lastMessageCompact: JSON.stringify(merged)
};

return {
  output: $input.first().json.output || JSON.parse($('Se tiver resumo').first().json.propertyName),
  chatInput: $('Webhook').first().json.body.message
}

