const inputData = $input.all()[0].json;
const workflow = $('BLUEPRINT ENGINE').first().json.workflow;
let currentStageId = $('BLUEPRINT ENGINE').first().json.currentStageId;
let vars = $('BLUEPRINT ENGINE').first().json.vars;
let stageConfig = $('BLUEPRINT ENGINE').first().json.stageConfig;
const lastInstruction = JSON.parse($('GET Estado Atual').first().json.propertyName || '{}').lastInstruction
const lastMissingVar = JSON.parse($('GET Estado Atual').first().json.propertyName || '{}').missingVar 
let outputInstruction = "";
let missingVar = "";
let immediatelyNextStage = "";

// --- 1. ATUALIZAÃ‡ÃƒO GLOBAL (Check-in) ---
const extractedObj = inputData.output || {};
for (const [key, value] of Object.entries(extractedObj)) {
    // SÃ³ atualiza se tiver valor real
    if (value !== null && value !== undefined && value !== "") {
        vars[key] = { value: value, type: 'extracted' };
    }
}

// Helper: Verifica se TODAS as variÃ¡veis do estÃ¡gio estÃ£o preenchidas
function isInputSatisfied(stage) {
    if (!stage || stage.type !== 'input') return false;
    // Retorna true apenas se TODAS as variÃ¡veis listadas existirem em 'vars'
    return stage.variables.every(v => vars[v.name] && vars[v.name].value !== null && vars[v.name].value !== "");
}


// --- 2. LOOP DE AVANÃ‡O (Auto-Skip & Look Ahead) ---
let safetyLoop = 0;
let advancing = true;

while (advancing && safetyLoop < 50) {
    stageConfig = workflow[currentStageId];
    if (!stageConfig) break;

    if (stageConfig.immediately_next_stage) {
      immediatelyNextStage = stageConfig.immediately_next_stage
    }

    advancing = false; // Por padrÃ£o, o loop para aqui, a menos que encontre motivo para seguir

    // ======================================================
    // CASO 1: INPUT (Esperando dados do usuÃ¡rio)
    // ======================================================
    if (stageConfig.type === 'input') {
        if (isInputSatisfied(stageConfig)) {
            // Temos tudo! Pula para o prÃ³ximo estÃ¡gio.
            currentStageId = stageConfig.next_stage;
            advancing = true; 
        } else {
            // [CORREÃ‡ÃƒO AQUI] Falta resposta. NÃ£o podemos deixar a instruÃ§Ã£o vazia.
            // Descobre qual variÃ¡vel estÃ¡ faltando para orientar a IA
            missingVar = stageConfig.variables.find(v => !vars[v.name] || !vars[v.name].value);

            if (missingVar) {
                // Se o usuÃ¡rio falou algo nada a ver (extraÃ§Ã£o rodou mas nÃ£o pegou o dado)
                if (Object.keys(extractedObj).length > 0 && !Object.values(extractedObj).find(v => v)) {
                     outputInstruction = `O usuÃ¡rio respondeu algo, mas nÃ£o entendi o(a) ${missingVar.description}. Pergunte novamente de forma clara.`;
                } else {
                    if (lastMissingVar && !vars[lastMissingVar.name]?.value) {
                      outputInstruction = lastInstruction
                    } else {
                      outputInstruction = `Pergunte: ${missingVar.description}`; 
                    }
                }
            }
            // Mantemos advancing = false para PARAR aqui e esperar o user responder
        }
    }

    // ======================================================
    // CASO 2: MENSAGEM (O robÃ´ fala)
    // ======================================================
    else if (stageConfig.type === 'message') {
        // [LOOK AHEAD]
        // Verifica se a mensagem serve para pedir um dado que JÃ TEMOS.
        const nextStage = workflow[stageConfig.next_stage];

        // Se o prÃ³ximo passo Ã© input E jÃ¡ estÃ¡ preenchido -> Pula a mensagem atual
        if (nextStage && nextStage.type === 'input' && isInputSatisfied(nextStage)) {
            currentStageId = nextStage.next_stage;
            advancing = true; 
        } else {
            // Mensagem necessÃ¡ria. Define instruÃ§Ã£o.
            outputInstruction = stageConfig.instruction;

            // JÃ¡ aponta o ponteiro para o prÃ³ximo estÃ¡gio (onde vamos esperar o input)
            if (stageConfig.next_stage !== 'STOP') {
                currentStageId = stageConfig.next_stage;
                const nextStage = workflow[currentStageId]   

                if (nextStage.type == 'input') {
                  missingVar = nextStage.variables.find(v => !vars[v.name] || !vars[v.name].value);
                }

            }
            // Para o loop para a IA falar.
            advancing = false; 
        }
    }

    // ======================================================
    // CASO 3: ROUTER (LÃ³gica condicional)
    // ======================================================
    else if (stageConfig.type === 'router') {
        let routeFound = false;
        for (const rule of stageConfig.rules) {
            if (rule.default) {
                currentStageId = rule.default;
                routeFound = true;
                break;
            }
            // ValidaÃ§Ã£o simples de valor
            if (vars[rule.variable] && vars[rule.variable].value === rule.value) {
                currentStageId = rule.next_stage;
                routeFound = true;
                break;
            }
        }
        // Se achou rota, continua avanÃ§ando no mesmo tick
        if (routeFound) advancing = true; 
    }

    safetyLoop++;
}

return {
    newStageId: currentStageId,
    updatedVars: vars,
    finalInstruction: outputInstruction,
    missingVar, 
    immediatelyNextStage
};

