function formatarSegundosParaMinutos(segundos) {
  const minutos = Math.floor(segundos / 60);
  const segundosRestantes = Math.floor(segundos % 60);
  return `${String(minutos).padStart(2, '0')}:${String(segundosRestantes).padStart(2, '0')}`;
}

return {
  messages: $('Get a transcript').first().json.data.transcript.sentences.map(message => {
    return formatarSegundosParaMinutos(message.start_time)+` | ${message.speaker_name}: ${message.raw_text}`
  })
}

