const participantes = $('Get a transcript').first().json.data.transcript.participants

const internos = [];
const externos = [];
const invalidEmail = (email) => !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email) || email === 'email@email.com';

for (const entry of participantes) {
  if (typeof entry === 'string') {
    const emails = entry.split(',').map(e => e.trim().toLowerCase());
    for (const email of emails) {
      if (invalidEmail(email)) continue;
      if (email.endsWith('@apresenta.me') && email !== 'notes@apresenta.me') {
        if (!internos.includes(email)) internos.push(email);
      } else if (!externos.includes(email) && !email.endsWith('@apresenta.me')) {
        externos.push(email);
      }
    }
  }
}

return {
  emailsInternos: internos,
  emailsCliente: externos
}

