const vars = $('Seta vari√°veis da conversa').item.json
const foundVariables = $input.first().json.output.foundVariables

for (const foundVariable of foundVariables) {
  const data = {
    name: foundVariable.variable, 
    value: foundVariable.value
  }

  if (vars[data.name]) {
    vars[data.name].value = data.value
  }  
}

return {
  updatedVars: foundVariables.map(v => v.variable), 
  vars
}

const stages = $('Seta est√°gios').item.json
const vars = $('Atualiza vari√°veis').item.json.vars
const stagesEntries = Object.entries(stages)

let currentStage = null
let currentInstruction = null
let currentVar = null
let finalInstruction = null

for (const [key, value] of stagesEntries) {
  value.actions.requiredDataForExecute = value.actions.requiredDataForExecute.map(v => {
    const varValue = vars[v.name].value
    return {
      ...v, 
      value: varValue || undefined
    }
  })

  const stagePendingVarIndex = value.actions.requiredDataForExecute.findIndex(v => !v.value)
  const stagePendingVar = value.actions.requiredDataForExecute[stagePendingVarIndex]

  if (stagePendingVar) {
    currentInstruction = stagePendingVar.instruction
    currentVar = stagePendingVar.name
    currentStage = {
      ...value, 
      name: key
    }
    break
  }

  if (!value.performed) {
    stages[key].performed = true
    finalInstruction = value.actions.finalInstructions

    if(!finalInstruction?.trim()) continue

    currentVar = null
    currentStage = {
      ...value,
      name: key
    }

    delete currentStage['performed']
  }
}

return {
  currentInstruction,
  finalInstruction, 
  currentStage: currentStage?.name || undefined,
  currentVar, 
  stages
}
