const vars = $('Atualiza vari√°veis').item ? $('Atualiza vari√°veis').item.json.vars : {}; 

const filteredStages = Object.entries(stages).filter(([key, stage]) => {

  if (stage.performed) return false;

  const conditions = stage.requiredDataForExecute;

  if (!conditions || !Array.isArray(conditions) || conditions.length === 0) {
    return true;
  }

  // Verifica se TODAS as condi√ß√µes s√£o atendidas
  const conditionsMet = conditions.every(condition => {
    const varObj = vars[condition.name];
    const actualValue = varObj ? varObj.value : null;

    switch (condition.operator) {
      case 'exist':
        return actualValue !== null && actualValue !== undefined && actualValue !== '';

