const stages = $('Busca est√°gio').first().json.stages;

const vars = $('Atualiza vari√°veis').item ? $('Atualiza vari√°veis').item.json.vars : {}; 

const filteredStages = Object.entries(stages).filter(([key, stage]) => {

  if (stage.performed) return false;

  const conditions = stage.requiredDataForExecute;

  if (!conditions || !Array.isArray(conditions) || conditions.length === 0) {
    return true;
  }

  // Verifica se TODAS as condi√ß√µes s√£o atendidas
  const conditionsMet = conditions.every(condition => {
    const varObj = vars[condition.name];
    const actualValue = varObj ? varObj.value : null;

    switch (condition.operator) {
      case 'exist':
        return actualValue !== null && actualValue !== undefined && actualValue !== '';

      case '!exist':
        return actualValue === null || actualValue === undefined || actualValue === '';

      case '=':
        return actualValue == condition.value;

      default:
        return true; 
    }
  });

  return conditionsMet;
});

return {
  unperfomedStages: filteredStages
}

const data = $input.all();
const formatChatHistory = (inputData) => {
  return inputData.map(jsonItem => {
    const item = jsonItem.json
    // 1. Define o remetente
    const sender = item.message.type === 'human' ? 'User' : 'IA';

    // 2. Pega o conte√∫do original
    let content = item.message.content;

    // 3. Se for IA, aplica a Regex para limpar o "lixo" t√©cnico
    if (item.message.type === 'ai') {
      // REGEX EXPLICADA:
      // ^              -> Come√ßa no in√≠cio da string
      // \[Used tools:  -> Procura literalmente por "[Used tools:"
      // [\s\S]*?       -> Pega qualquer caractere (incluindo quebra de linha) de forma n√£o-gulosa (para no primeiro match)
      // \]\]           -> Procura o fechamento dos colchetes duplos "]]"
      // \s* -> Remove quaisquer espa√ßos ou quebras de linha que sobrem logo ap√≥s
      content = content.replace(/^\[Used tools:[\s\S]*?\]\]\s*/, '');
    }

    // 4. Retorna no formato solicitado
    return `${sender}: ${content}`;
  }).join('\n'); // Une tudo com quebra de linha
};

// Executa e mostra o resultado
const result = formatChatHistory(data);

return  {
  result
}
