function removeEmojis(str) {
  return str.replace(
    /([\u2700-\u27BF]|[\uE000-\uF8FF]|[\uD83C-\uDBFF][\uDC00-\uDFFF]|[\u2600-\u26FF]|[\uFE00-\uFE0F])/g,
    ''
  );
}


function markdownToBBCode(markdown) {
  return markdown
    // T√≠tulos
    .replace(/^### (.*$)/gim, '[b]$1[/b]')
    .replace(/^## (.*$)/gim, '[b]$1[/b]')
    .replace(/^# (.*$)/gim, '[b]$1[/b]')

    // Negrito
    .replace(/\*\*(.*?)\*\*/g, '[b]$1[/b]')

    // It√°lico (opcional, se quiser suportar *texto*)
    .replace(/\*(.*?)\*/g, '[i]$1[/i]')

    // Quebras de linha duplas viram par√°grafo
    .replace(/\n{2,}/g, '\n\n')
}

function markdownToHTML(markdown) {
  // Corrige \\n (string vinda de JSON bruto)
  markdown = markdown.replace(/\\n/g, '<br>');

  // Converte t√≠tulos
  markdown = markdown
    .replace(/^### (.*)$/gim, '<h3>$1</h3>')
    .replace(/^## (.*)$/gim, '<h2>$1</h2>')
    .replace(/^# (.*)$/gim, '<h1>$1</h1>');

  // Negrito e it√°lico
  markdown = markdown
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
    .replace(/\*(.*?)\*/g, '<em>$1</em>');

  // Transforma par√°grafos (dupla quebra de linha) em <p>
  let html = markdown
    .split(/\n{2,}/)
    .map(block => {
      // Se for t√≠tulo, n√£o envolve em <p>
      if (/^\s*<h\d>/.test(block)) return block.trim();
      return `<p>${block.trim().replace(/\n/g, '<br>')}</p>`;
    })
    .join('\n');

  return html;
}



return {
  html: markdownToHTML($input.first().json.email)
}
