let missingVars = [];
for (const key in workflow) {
    const step = workflow[key];
    if (step.type === 'input' && step.variables) {
        step.variables.forEach(v => {
            if (!vars[v.name] || vars[v.name].value === null) {
                missingVars.push({
                    name: v.name,
                    description: v.description
                });
            }
        });
    }
}

return {
  workflow,
  currentStageId,
  stageConfig,
  vars,
  missingVars,
  userMsg: $('CONCATENA1').item.json.user_full_message
};

return { 
  cache_key: `session_${$json.body.ticketId}`,
  companyId: $json.body.companyId,
  whatsappId: $json.body.whatsappId,
  user_msg: $json.body.message,
  user_phone: $json.body.contact.number,
  timestamp: new Date().getTime()
};

const messages = $input.all()

if (!Object.entries(messages).length) return { historyMessages: null }

const lastMessagesSorted = messages.reverse()

const historyMessages = lastMessagesSorted.map(m => {
  return `**${m.json.message?.type?.toLocaleLowerCase()}**: ${m.json.message?.content}`
}).join('\n\n')

return {
  historyMessages 
}
