const campaigns = $input.first().json.data
const insights = $('Busca insights').first().json.data

for(const campaign of campaigns) {
  const campaignInsights = insights.find(i => String(i.campaign_id) === String(campaign.id)) 
  campaign.insights = campaignInsights
}

return {
  campaigns
}

const period_type_translated = 'Di√°rio'
return {
  date_preset: "yesterday",
  period_type: "daily",
  period_type_translated,
  text: (() => {
    const d = new Date();
    d.setDate(d.getDate() - 1);
    const fmt = x => `${String(x.getDate()).padStart(2,'0')}/${String(x.getMonth()+1).padStart(2,'0')}/${x.getFullYear()}`;
    return `${period_type_translated} - ${fmt(d)}`;
  })()
};

const period_type_translated = 'Semanal'
return {
  date_preset: "last_7d",
  period_type: "weekly",
  period_type_translated,
  text: (() => {
    const end = new Date();
    const start = new Date();
    start.setDate(end.getDate() - 7);
    const fmt = d => `${String(d.getDate()).padStart(2,'0')}/${String(d.getMonth()+1).padStart(2,'0')}/${d.getFullYear()}`;
    return `${period_type_translated} - ${fmt(start)} at√© ${fmt(end)}`;
  })()
};
